mapper.select:
SELECT MNAME, EMAIL
FROM MEMBER
WHERE EMAIL = #{email} AND PWD = #{pwd}

SELECT 	MNO, MNAME, EMAIL, CRE_DATE
FROM 	MEMBER
WHERE 	MNO = #{no}

mapper.update:
UPDATE MEMBER
MNAME = #{name}, EMAIL = #{email}, MOD_DATE = SYSDATE
WHERE MNO = #{no}

mapper.select:
SELECT IDX, STORED_FILE_NAME
FROM TB_FILE
WHERE PARENT_SEQ = #{parentSeq}
